'use strict';

/**
 *  Module dependencies.
 */

var env = process.env;
var pkg = require('../package');

const PORT    = env.PORT || 3000;
const ENV     = env.NODE_ENV || 'development';
const isProd  = ENV === 'production';

var postgre = require('./database')[ENV];
var session = require('./session')[ENV];

var config = Object.freeze({
  env           : ENV,
  version       : pkg.version,
  debug         : true,
  isProduction  : isProd,
  isDevelopment : !isProd,
  port          : PORT,

  postgre       : postgre,
  session       : session,
  sessionSecret : 'i â™¥ the starship',

  // https://www.owasp.org/index.php/Password_Storage_Cheat_Sheet
  pbkdf2    : {
    keylen    : 64,
    saltlen   : 32,
    algorithm : 'sha256',
    iterations: isProd ? 6400 : 10
  }
});

module.exports = config;
